import { StatusBar, StyleSheet, Text, SafeAreaView, Image, Dimensions } from 'react-native';

import topo from './assets/topo.png'
import logoVendedor from './assets/logo.png'
import { View } from 'react-native-web';

const width = Dimensions.get('screen').width;

export default function App() {
  return (
    <SafeAreaView style={styles.container}>
      <Image style = {styles.topo} source={topo} />
      <Text style = {styles.tituloCarrinho}>Carrinho</Text>

      <Text style = {styles.detalhes}>Detalhes do Carrinho</Text>
      <Text style = {styles.titulo}>Kit IoT</Text>
      <View>
        <Image style={styles.logoVendedor} source={logoVendedor}/>
        <Text style = {styles.nomeVendedor}>Nome do Vendedor</Text>
      </View>
      <Text style = {styles.desc}>Um kit IoT que revoluciona a vida dos estudantes</Text>
      <Text style = {styles.preco}>R$ 99,00</Text>

      <StatusBar />
    </SafeAreaView>
  );
}

const styles = StyleSheet.create({
  container: {
    flex: 1,
    backgroundColor: '#fff',

  },
  topo: {
    width:"100%",
    height:582/772 * width
  },
  tituloCarrinho: {
    position:"absolute",
    paddingVertical: 60,
    textAlign:"center",
    width:"100%",
    fontSize: 20,
    fontWeight:"bold",
    lineHeight:40
  },
  detalhes: {
    fontWeight:"bold",
    fontSize: 25,
    lineHeight: 35,
    textAlign:"center"
  },
  titulo: {
    color:"#ffa500",
    fontWeight:"bold",
    fontSize: 25,
    lineHeight: 35,
    textAlign:"center"
  },
  nomeVendedor: {
    color:"#a3a3a3",
    fontSize:18,
    lineHeight:26
  },
  desc: {
    fontSize:16,
    paddingVertical:10
  },
  preco: {
    fontSize:35,
    fontWeight:"bold",
    textAlign:"right",
    paddingHorizontal:10
  },
  logoVendedor: {
    width:32,
    height: 32
  }
});
